DROP DATABASE IF EXISTS GLO2005_TP;
CREATE DATABASE IF NOT EXISTS GLO2005_TP;
USE GLO2005_TP;
CREATE TABLE IF NOT EXISTS humans (id INT NOT NULL AUTO_INCREMENT, username VARCHAR(100), password VARCHAR(100), PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS admins (id INT NOT NULL AUTO_INCREMENT, name VARCHAR(100), password VARCHAR(100),PRIMARY KEY (id), FOREIGN KEY (id) REFERENCES humans(id));
CREATE TABLE IF NOT EXISTS vendors (id INT NOT NULL AUTO_INCREMENT, name VARCHAR(100), password VARCHAR(100), username VARCHAR(100), description VARCHAR(100), address VARCHAR(100),PRIMARY KEY (id), FOREIGN KEY (id) REFERENCES humans(id));
CREATE TABLE IF NOT EXISTS customers (id INT NOT NULL AUTO_INCREMENT, first_name VARCHAR(100), last_name VARCHAR(100), address VARCHAR(100), phone_number VARCHAR(100), email VARCHAR(100),PRIMARY KEY (id), FOREIGN KEY (id) REFERENCES humans(id));
CREATE TABLE IF NOT EXISTS products (id INT NOT NULL AUTO_INCREMENT, name VARCHAR(100), description VARCHAR(100), price FLOAT, category_id INT,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS carts (id INT NOT NULL AUTO_INCREMENT, total_cost FLOAT,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS payment_systems (id INT NOT NULL AUTO_INCREMENT, payment_type VARCHAR(100),PRIMARY KEY (id));
